// Initialize map
var map = L.map("map").setView([0, 0], 2); // Centered at [latitude, longitude], zoom level 2

// Add tile layer (e.g., OpenStreetMap)
L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
  maxZoom: 19,
  attribution:
    'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
}).addTo(map);
// Example locations (latitude, longitude)
var locations = [
  [51.505, -0.09], // London
  [40.7128, -74.006], // New York
  // Add more locations as needed
];

// Add markers or circles for each location
locations.forEach(function (location) {
  L.marker(location).addTo(map); // Marker
  // L.circle(location, { radius: 10000 }).addTo(map); // Circle with radius (optional)
});
// Add popups to markers
var popupContent = "<b>Hello, world!</b><br>I am a popup.";
L.marker([51.505, -0.09]).bindPopup(popupContent).addTo(map);

// Handle marker click events
function onMarkerClick(e) {
  alert("Marker clicked at: " + e.latlng);
}
map.on("click", onMarkerClick);
